    font-weight: 600;
    letter-spacing: -0.02em;
  }
}

/* Utility classes */
.font-mono {
  font-family: var(--font-mono);
}
.shadow-card {
  box-shadow: var(--shadow-card);
}
.shadow-hero {
  box-shadow: var(--shadow-hero);
}
.gradient-hero {
  background: var(--gradient-hero);
}
.stat-number {
  font-family: var(--font-mono);
  font-weight: 500;
  letter-spacing: -0.03em;
}

